def ask_question_to_gemini_cache(prompt, max_retries=5, retry_delay=5): """ Gemini API를 사용하여 질문에 대한 답변을 얻습니다. 뉴스 분석에 최적화된 버전입니다. """ start_time = time.time()
for attempt in range(max_retries): try: # API 키 확인 api_key = os.getenv("GOOGLE_AI_API_KEY") if not api_key: raise Exception("GOOGLE_AI_API_KEY 환경 변수가 설정되지 않았습니다.")
api_start = time.time()
# Gemini API 호출 response = client.models.generate_content( model=model_name, contents=prompt, config=types.GenerateContentConfig( temperature=0.3, max_output_tokens=2048 ) )
return response.text
except Exception as e: error_msg = str(e).lower() print(f"API 오류 (시도 {attempt + 1}/{max_retries}): {e}")
if hasattr(e, 'code') and e.code == 503: print(f"⏳ API 사용량 한도 초과 (시도 {attempt + 1}/{max_retries}). {retry_delay}초 후 재시도...") time.sleep(retry_delay) retry_delay *= 2 # 지수적 백오프 continue
if attempt == max_retries - 1: raise Exception(f"최대 재시도 횟수 초과. 마지막 오류: {e}")
time.sleep(retry_delay)
raise Exception("모든 재시도 실패")
def json_match(text): """ 텍스트에서 JSON 객체를 추출하는 함수 """ try: # 중괄호 패턴 매칭 pattern = r'\{[^{}]*(?:\{[^{}]*\}[^{}]*)*\}' matches = re.findall(pattern, text, re.DOTALL)
for match in matches: try: return json.loads(match) except json.JSONDecodeError: continue
# 백틱으로 감싸진 JSON 찾기 json_pattern = r'```(?:json)?\s*(\{.*?\})\s*```' matches = re.findall(json_pattern, text, re.DOTALL)
for match in matches: try: return json.loads(match) except json.JSONDecodeError: continue
return None except Exception as e: print(f"JSON 파싱 오류: {e}") return None
